import React, { useState, useEffect } from 'react';

const OrderPagee = ({ selectedOrderId }) => {
  const [orders, setOrders] = useState([]);
  const [expandedOrder, setExpandedOrder] = useState(null);

  // Auto-expand the order if selectedOrderId is provided
  useEffect(() => {
    if (selectedOrderId) {
      console.log('🔔 Auto-expanding order:', selectedOrderId);
      setExpandedOrder(selectedOrderId);

      // Optional: Scroll to the expanded order
      setTimeout(() => {
        const element = document.getElementById(`order-${selectedOrderId}`);
        if (element) {
          element.scrollIntoView({ behavior: 'smooth', block: 'center' });
        }
      }, 500);
    }
  }, [selectedOrderId]);

  // Your existing order fetching logic here
  useEffect(() => {
    fetchOrders();
  }, []);

  const fetchOrders = async () => {
    try {
      const user = JSON.parse(localStorage.getItem('user'));
      const response = await fetch(`/api/orders/my-orders?user_id=${user.id}`);
      const ordersData = await response.json();
      setOrders(ordersData);
    } catch (error) {
      console.error('Error fetching orders:', error);
    }
  };

  const toggleOrderExpand = (orderId) => {
    setExpandedOrder(expandedOrder === orderId ? null : orderId);
  };

  return (
    <div className="order-page">
      <h2>My Orders</h2>

      {selectedOrderId && (
        <div className="notification-highlight">
          📍 Showing order from notification
        </div>
      )}

      <div className="orders-list">
        {orders.map(order => (
          <div 
            key={order.id} 
            id={`order-${order.id}`}
            className={`order-card ${expandedOrder === order.id ? 'expanded' : ''} ${selectedOrderId === order.id ? 'highlighted' : ''}`}
          >
            <div className="order-header" onClick={() => toggleOrderExpand(order.id)}>
              <div className="order-info">
                <span className="order-id">Order #{order.id}</span>
                <span className="order-amount">{order.amount} {order.currency_from}</span>
                <span className={`order-status ${order.status}`}>{order.status}</span>
              </div>
              <div className="order-arrow">
                {expandedOrder === order.id ? '▲' : '▼'}
              </div>
            </div>

            {expandedOrder === order.id && (
              <div className="order-details">
                <div className="detail-row">
                  <span>Amount:</span>
                  <span>{order.amount} {order.currency_from}</span>
                </div>
                <div className="detail-row">
                  <span>Total Price:</span>
                  <span>{order.total_price} {order.currency_to}</span>
                </div>
                <div className="detail-row">
                  <span>Status:</span>
                  <span className={`status-badge ${order.status}`}>{order.status}</span>
                </div>
                <div className="detail-row">
                  <span>Counterparty:</span>
                  <span>{order.buyer_id === user.id ? order.seller_name : order.buyer_name}</span>
                </div>
                {/* Add more order details as needed */}
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default OrderPagee;
