import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import io from 'socket.io-client';
import ReactCrop from 'react-image-crop';
import 'react-image-crop/dist/ReactCrop.css';
import Modal from 'react-modal';

// Set app element for accessibility
Modal.setAppElement('#root');

const ChatModal = ({ order, user, onClose }) => {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [loading, setLoading] = useState(true);
  const [socket, setSocket] = useState(null);
  const [typingUsers, setTypingUsers] = useState([]);
  const [connectionError, setConnectionError] = useState('');
  const [uploading, setUploading] = useState(false);
  const [selectedFile, setSelectedFile] = useState(null);
  const [filePreview, setFilePreview] = useState(null);
  const [cropModalOpen, setCropModalOpen] = useState(false);
  const [crop, setCrop] = useState({ unit: '%', width: 100, height: 100 });
  const [completedCrop, setCompletedCrop] = useState(null);
  const [imageRef, setImageRef] = useState(null);
  const [fileCaption, setFileCaption] = useState('');
  const fileInputRef = useRef(null);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    // Initialize socket connection
    const newSocket = io('http://localhost:50000', {
      transports: ['websocket', 'polling']
    });

    newSocket.on('connect', () => {
      console.log('ðŸ”µ Socket connected:', newSocket.id);
      setConnectionError('');
    });

    newSocket.on('connect_error', (error) => {
      console.error('ðŸ”´ Socket connection error:', error);
      setConnectionError('Failed to connect to chat server');
    });

    newSocket.on('disconnect', (reason) => {
      console.log('ðŸ”´ Socket disconnected:', reason);
    });

    setSocket(newSocket);

    // Load chat messages
    loadMessages();

    // Join order room after connection is established
    if (newSocket.connected) {
      joinOrderRoom(newSocket);
    } else {
      newSocket.on('connect', () => {
        joinOrderRoom(newSocket);
      });
    }

    // Listen for new messages
    newSocket.on('receive_message', (message) => {
      console.log('ðŸ’¬ New message received:', message);
      setMessages(prev => [...prev, message]);
    });

    // Listen for typing indicators
    newSocket.on('user_typing', (data) => {
      console.log('âŒ¨ï¸ User typing:', data);
      setTypingUsers(prev => {
        if (!prev.find(user => user.userId === data.userId)) {
          return [...prev, data];
        }
        return prev;
      });
    });

    newSocket.on('user_stop_typing', (data) => {
      console.log('âŒ¨ï¸ User stopped typing:', data);
      setTypingUsers(prev => prev.filter(user => user.userId !== data.userId));
    });

    return () => {
      console.log('ðŸ”´ Cleaning up socket connection');
      newSocket.disconnect();
    };
  }, [order.id]);

  const joinOrderRoom = (socket) => {
    console.log('ðŸŸ¡ Joining order room:', order.id);
    socket.emit('join_order', order.id);
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages, typingUsers]);

  const loadMessages = async () => {
    try {
      console.log('ðŸŸ¡ Loading messages for order:', order.id);
      const response = await axios.get(`http://localhost:50000/api/chat/messages/${order.id}`);
      console.log('ðŸ“¨ Loaded messages:', response.data);

      // Parse file_data from JSON string
      const messagesWithFiles = response.data.map(message => ({
        ...message,
        file_data: message.file_data ? JSON.parse(message.file_data) : null
      }));

      setMessages(messagesWithFiles);
    } catch (error) {
      console.error('ðŸ”´ Error loading messages:', error);
    } finally {
      setLoading(false);
    }
  };

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const handleFileSelect = (e) => {
    const file = e.target.files[0];
    if (file) {
      // Check if file is an image
      if (file.type.startsWith('image/')) {
        setSelectedFile(file);
        // Create preview
        const reader = new FileReader();
        reader.onload = (e) => {
          setFilePreview(e.target.result);
          setCropModalOpen(true);
        };
        reader.readAsDataURL(file);
      } else {
        // For non-image files, skip cropping
        setSelectedFile(file);
        setFilePreview(URL.createObjectURL(file));
        setCropModalOpen(true);
      }
    }
    // Reset file input
    e.target.value = '';
  };

  const getCroppedImg = (image, crop, fileName) => {
    const canvas = document.createElement('canvas');
    const ctx = canvas.getContext('2d');

    const scaleX = image.naturalWidth / image.width;
    const scaleY = image.naturalHeight / image.height;
    const pixelRatio = window.devicePixelRatio;

    canvas.width = crop.width * pixelRatio * scaleX;
    canvas.height = crop.height * pixelRatio * scaleY;

    ctx.setTransform(pixelRatio, 0, 0, pixelRatio, 0, 0);
    ctx.imageSmoothingQuality = 'high';

    ctx.drawImage(
      image,
      crop.x * scaleX,
      crop.y * scaleY,
      crop.width * scaleX,
      crop.height * scaleY,
      0,
      0,
      crop.width * scaleX,
      crop.height * scaleY
    );

    return new Promise((resolve) => {
      canvas.toBlob(
        (blob) => {
          if (!blob) {
            console.error('Canvas is empty');
            return;
          }
          blob.name = fileName;
          resolve(blob);
        },
        selectedFile.type,
        1
      );
    });
  };

  const handleCropComplete = async () => {
    if (imageRef && completedCrop && selectedFile.type.startsWith('image/')) {
      try {
        const croppedImageBlob = await getCroppedImg(
          imageRef,
          completedCrop,
          selectedFile.name
        );
        setSelectedFile(new File([croppedImageBlob], selectedFile.name, { type: selectedFile.type }));
      } catch (error) {
        console.error('Error cropping image:', error);
      }
    }
    setCropModalOpen(false);
  };

  const handleSendFile = async () => {
    if (!selectedFile || !socket) return;

    setUploading(true);
    try {
      const formData = new FormData();
      formData.append('file', selectedFile);

      console.log('ðŸ“¤ Uploading file:', selectedFile.name);

      const uploadResponse = await axios.post('http://localhost:50000/api/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      console.log('ðŸŸ¢ File uploaded:', uploadResponse.data);

      // Send file message via socket
      if (socket && socket.connected) {
        const messageData = {
          orderId: order.id,
          senderId: user.id,
          senderName: user.name,
          message: fileCaption || selectedFile.name,
          messageType: 'file',
          file: uploadResponse.data.file
        };

        console.log('ðŸ“¤ Sending file message:', messageData);
        socket.emit('send_message', messageData);

        // Reset file state
        setSelectedFile(null);
        setFilePreview(null);
        setFileCaption('');
      }

    } catch (error) {
      console.error('ðŸ”´ File upload error:', error);
      alert('Failed to upload file. Please try again.');
    } finally {
      setUploading(false);
    }
  };

  const cancelFileSend = () => {
    setSelectedFile(null);
    setFilePreview(null);
    setFileCaption('');
    setCropModalOpen(false);
  };

  const sendMessage = (e) => {
    e.preventDefault();
    if (!newMessage.trim() || !socket) {
      console.log('ðŸ”´ Cannot send message - no socket or empty message');
      return;
    }

    if (!socket.connected) {
      setConnectionError('Not connected to chat server. Please try again.');
      return;
    }

    const messageData = {
      orderId: order.id,
      senderId: user.id,
      senderName: user.name,
      message: newMessage.trim(),
      messageType: 'text'
    };

    console.log('ðŸ“¤ Sending message:', messageData);
    socket.emit('send_message', messageData);
    setNewMessage('');
  };

  const handleTyping = () => {
    if (socket && socket.connected) {
      socket.emit('typing', {
        orderId: order.id,
        userId: user.id,
        userName: user.name
      });

      // Stop typing after 1 second
      setTimeout(() => {
        if (socket && socket.connected) {
          socket.emit('stop_typing', {
            orderId: order.id,
            userId: user.id
          });
        }
      }, 1000);
    }
  };

  const formatTime = (timestamp) => {
    return new Date(timestamp).toLocaleTimeString([], { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };

  const isMyMessage = (message) => {
    return message.sender_id === user.id;
  };

  const getOtherPartyName = () => {
    if (user.id === order.buyer_id) {
      return order.seller_name;
    } else {
      return order.buyer_name;
    }
  };

  const getFileIcon = (mimetype) => {
    if (mimetype.startsWith('image/')) return 'ðŸ–¼ï¸';
    if (mimetype === 'application/pdf') return 'ðŸ“„';
    if (mimetype.includes('word') || mimetype.includes('document')) return 'ðŸ“';
    if (mimetype === 'text/plain') return 'ðŸ“ƒ';
    return 'ðŸ“Ž';
  };

  const formatFileSize = (bytes) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  if (loading) {
    return (
      <div className="modal-overlay">
        <div className="modal-content">
          <div className="loading">Loading chat...</div>
        </div>
      </div>
    );
  }

  return (
    <div className="modal-overlay">
      <div className="modal-content chat-modal">
        <div className="chat-header">
          <div className="chat-title">
            <h3>ðŸ’¬ Trade Chat - Order #{order.id}</h3>
            <div className="order-info">
              Chatting with: {getOtherPartyName()}
            </div>
            <div className="debug-info">
              <small>Your ID: {user.id} | Order ID: {order.id} | Socket: {socket?.connected ? 'ðŸŸ¢ Connected' : 'ðŸ”´ Disconnected'}</small>
            </div>
          </div>
          <button className="close-btn" onClick={onClose}>Ã—</button>
        </div>

        {connectionError && (
          <div className="error-message">
            {connectionError}
          </div>
        )}

        <div className="chat-order-details">
          <div className="trade-summary">
            <span>{order.amount} {order.currency_from}</span>
            <span className="arrow">â†’</span>
            <span>{order.total_price} {order.currency_to}</span>
          </div>
          <div className="order-status">
            Status: <span className={`status-${order.status}`}>{order.status}</span>
          </div>
        </div>

        <div className="chat-messages">
          {messages.length === 0 ? (
            <div className="no-messages">
              <p>No messages yet. Start the conversation!</p>
            </div>
          ) : (
            messages.map((message) => (
              <div
                key={message.id}
                className={`message ${isMyMessage(message) ? 'my-message' : 'other-message'}`}
              >
                <div className="message-content">
                  {message.message_type === 'file' && message.file_data ? (
                    <div className="file-message">
                      <div className="file-info">
                        <span className="file-icon">
                          {getFileIcon(message.file_data.mimetype)}
                        </span>
                        <div className="file-details">
                          <div className="file-name">{message.file_data.originalname}</div>
                          <div className="file-size">{formatFileSize(message.file_data.size)}</div>
                          {message.message && message.message !== message.file_data.originalname && (
                            <div className="file-caption">{message.message}</div>
                          )}
                        </div>
                      </div>
                      {message.file_data.mimetype.startsWith('image/') ? (
                        <div className="image-preview-container">
                          <img 
                            src={`http://localhost:50000${message.file_data.path}`}
                            alt={message.file_data.originalname}
                            className="image-preview blurred"
                            onLoad={(e) => {
                              // Remove blur after image loads
                              setTimeout(() => {
                                e.target.classList.remove('blurred');
                              }, 1000);
                            }}
                          />
                          <a 
                            href={`http://localhost:50000${message.file_data.path}`} 
                            target="_blank" 
                            rel="noopener noreferrer"
                            className="download-btn"
                          >
                            View Original
                          </a>
                        </div>
                      ) : (
                        <a 
                          href={`http://localhost:50000${message.file_data.path}`} 
                          target="_blank" 
                          rel="noopener noreferrer"
                          className="download-btn"
                        >
                          Download
                        </a>
                      )}
                    </div>
                  ) : (
                    <div className="message-text">{message.message}</div>
                  )}
                  <div className="message-time">
                    {formatTime(message.created_at)}
                    {!isMyMessage(message) && (
                      <span className="sender-name"> â€¢ {message.sender_name}</span>
                    )}
                  </div>
                </div>
              </div>
            ))
          )}

          {typingUsers.length > 0 && (
            <div className="typing-indicator">
              {typingUsers.map(user => (
                <div key={user.userId} className="typing-text">
                  {user.userName} is typing...
                </div>
              ))}
            </div>
          )}

          <div ref={messagesEndRef} />
        </div>

        {/* File Preview Section */}
        {selectedFile && !cropModalOpen && (
          <div className="file-preview-section">
            <div className="file-preview-header">
              <h4>ðŸ“Ž File Preview</h4>
              <button className="cancel-file-btn" onClick={cancelFileSend}>Ã—</button>
            </div>
            <div className="file-preview-content">
              {selectedFile.type.startsWith('image/') ? (
                <img src={filePreview} alt="Preview" className="file-preview-image" />
              ) : (
                <div className="file-preview-generic">
                  <span className="file-icon-large">{getFileIcon(selectedFile.type)}</span>
                  <div className="file-details">
                    <div className="file-name">{selectedFile.name}</div>
                    <div className="file-size">{formatFileSize(selectedFile.size)}</div>
                  </div>
                </div>
              )}
              <div className="file-caption-input">
                <input
                  type="text"
                  value={fileCaption}
                  onChange={(e) => setFileCaption(e.target.value)}
                  placeholder="Add a caption (optional)"
                  maxLength="200"
                />
              </div>
              <div className="file-actions">
                <button 
                  className="cancel-btn" 
                  onClick={cancelFileSend}
                  disabled={uploading}
                >
                  Cancel
                </button>
                <button 
                  className="send-file-btn" 
                  onClick={handleSendFile}
                  disabled={uploading}
                >
                  {uploading ? 'ðŸ“¤ Sending...' : 'ðŸ“¤ Send File'}
                </button>
              </div>
            </div>
          </div>
        )}

        <form onSubmit={sendMessage} className="chat-input-form">
          <div className="input-group">
            <input
              type="file"
              ref={fileInputRef}
              onChange={handleFileSelect}
              accept=".jpg,.jpeg,.png,.gif,.pdf,.doc,.docx,.txt"
              style={{ display: 'none' }}
            />

            <button 
              type="button" 
              className="file-btn"
              onClick={() => fileInputRef.current?.click()}
              disabled={uploading || !socket?.connected || selectedFile}
            >
              ðŸ“Ž
            </button>

            <input
              type="text"
              value={newMessage}
              onChange={(e) => {
                setNewMessage(e.target.value);
                handleTyping();
              }}
              placeholder="Type your message..."
              maxLength="500"
              disabled={!socket?.connected || selectedFile}
            />

            <button 
              type="submit" 
              disabled={!newMessage.trim() || !socket?.connected || selectedFile}
            >
              {socket?.connected ? 'Send' : 'Connecting...'}
            </button>
          </div>

          <div className="file-types-info">
            <small>Supported: Images (JPG, PNG, GIF), PDF, DOC, DOCX, TXT (Max 10MB)</small>
          </div>
        </form>

        {/* Crop Modal */}
        <Modal
          isOpen={cropModalOpen}
          onRequestClose={cancelFileSend}
          className="crop-modal"
          overlayClassName="crop-modal-overlay"
        >
          <div className="crop-modal-content">
            <div className="crop-modal-header">
              <h3>âœ‚ï¸ Crop Image</h3>
              <button className="close-btn" onClick={cancelFileSend}>Ã—</button>
            </div>
            <div className="crop-area">
              {selectedFile?.type.startsWith('image/') ? (
                <ReactCrop
                  crop={crop}
                  onChange={(newCrop) => setCrop(newCrop)}
                  onComplete={(c) => setCompletedCrop(c)}
                  aspect={1}
                >
                  <img
                    ref={setImageRef}
                    src={filePreview}
                    alt="Crop preview"
                    onLoad={(e) => {
                      const { width, height } = e.currentTarget;
                      setCrop({
                        unit: '%',
                        width: 90,
                        height: (90 * height) / width,
                      });
                    }}
                  />
                </ReactCrop>
              ) : (
                <div className="file-preview-generic">
                  <span className="file-icon-large">{getFileIcon(selectedFile?.type)}</span>
                  <div className="file-details">
                    <div className="file-name">{selectedFile?.name}</div>
                    <div className="file-size">{formatFileSize(selectedFile?.size)}</div>
                  </div>
                </div>
              )}
            </div>
            <div className="crop-actions">
              <button className="cancel-btn" onClick={cancelFileSend}>
                Cancel
              </button>
              <button className="confirm-btn" onClick={handleCropComplete}>
                {selectedFile?.type.startsWith('image/') ? 'Apply Crop' : 'Continue'}
              </button>
            </div>
          </div>
        </Modal>
      </div>
    </div>
  );
};

export default ChatModal;
