import React, { useState } from 'react';
import './App.css';

function App() {
  const [message, setMessage] = useState('');
  const [serverResponse, setServerResponse] = useState('');

  // Function to test connection to backend
  const testServer = async () => {
    try {
      const response = await fetch('http://localhost:50000/api/test');
      const data = await response.json();
      setServerResponse(data.message);
    } catch (error) {
      setServerResponse('‚ùå Cannot connect to server');
    }
  };

  return (
    <div className="app">
      <div className="container">
        <h1>Welcome to Your P2P Exchange!</h1>
        <p>This is your starting point.</p>

        <div className="card">
          <h2>Test Server Connection</h2>
          <button onClick={testServer}>Click to Test Backend</button>
          {serverResponse && <p className="response">{serverResponse}</p>}
        </div>

        <div className="card">
          <h2>Next Steps</h2>
          <ul>
            <li>‚úÖ Backend server setup</li>
            <li>‚úÖ Frontend React setup</li>
            <li>üî≤ User authentication</li>
            <li>üî≤ Database setup</li>
            <li>üî≤ KYC verification</li>
            <li>üî≤ Marketplace</li>
          </ul>
        </div>
      </div>
    </div>
  );
}

export default App;
