import React, { useState, useEffect, createContext, useContext } from 'react';
import { QueryClient, QueryClientProvider } from '@tanstack/react-query';
import { io } from 'socket.io-client';
import Login from './components/Login';
import Register from './components/Register';
import Dashboard from './components/Dashboard';
import './App.css';

// ✅ CREATE NOTIFICATION CONTEXT
const NotificationContext = createContext();

// ✅ CUSTOM HOOK FOR NOTIFICATIONS
export const useNotifications = () => {
  const context = useContext(NotificationContext);
  if (!context) {
    throw new Error('useNotifications must be used within NotificationProvider');
  }
  return context;
};

// ✅ NOTIFICATION PROVIDER COMPONENT
const NotificationProvider = ({ children }) => {
  const [notifications, setNotifications] = useState([]);
  const [unreadCount, setUnreadCount] = useState(0);
  const [socket, setSocket] = useState(null);

  // Initialize socket connection
  useEffect(() => {
    const newSocket = io('http://localhost:50000'); // Your server URL
    setSocket(newSocket);

    return () => {
      newSocket.disconnect();
    };
  }, []);

  // ✅ IMPROVED NOTIFICATION SOUND FUNCTION
  const playNotificationSound = () => {
    // Method 1: Try to play MP3 file
    try {
      const audio = new Audio('/notification.mp3');
      audio.volume = 0.3; // Set volume to 30%
      audio.play().catch(err => {
        console.log('MP3 audio play failed, trying fallback:', err);
        // If MP3 fails, use browser-generated sound
        playBrowserSound();
      });
    } catch (error) {
      console.log('MP3 audio error, using fallback:', error);
      playBrowserSound();
    }
  };

  const handleNotificationClick = (notification) => {
    console.log('🔔 Notification clicked:', notification);

    // Mark as read first
    markAsRead(notification.id);

    // Prepare redirect data
    let redirectData = {
      tab: 'orders', // default tab
      orderId: null
    };

    switch (notification.type) {
      case 'new_order':
      case 'order_status':
        redirectData.tab = 'orders';
        redirectData.orderId = notification.data?.id || notification.data?.order_id;
        break;

      case 'new_message':
        redirectData.tab = 'chat';
        redirectData.orderId = notification.data?.order_id;
        break;

      default:
        redirectData.tab = 'orders';
    }

    // Store in localStorage for Dashboard to read
    localStorage.setItem('notificationRedirect', JSON.stringify(redirectData));

    // Dispatch custom event for real-time navigation
    const event = new CustomEvent('notificationNavigate', {
      detail: redirectData
    });
    window.dispatchEvent(event);

    console.log('🔔 Navigation triggered:', redirectData);
  };

  // ✅ BROWSER-GENERATED SOUND (FALLBACK - NO FILE NEEDED)
  const playBrowserSound = () => {
    try {
      // Create audio context
      const AudioContext = window.AudioContext || window.webkitAudioContext;
      const audioCtx = new AudioContext();

      // Create oscillator for beep sound
      const oscillator = audioCtx.createOscillator();
      const gainNode = audioCtx.createGain();

      oscillator.connect(gainNode);
      gainNode.connect(audioCtx.destination);

      // Configure the beep sound
      oscillator.frequency.setValueAtTime(800, audioCtx.currentTime); // Frequency in Hz
      oscillator.type = 'sine';

      gainNode.gain.setValueAtTime(0.3, audioCtx.currentTime); // Volume
      gainNode.gain.exponentialRampToValueAtTime(0.01, audioCtx.currentTime + 0.3);

      oscillator.start(audioCtx.currentTime);
      oscillator.stop(audioCtx.currentTime + 0.3);

    } catch (error) {
      console.log('Browser sound also failed:', error);
    }
  };

  // ✅ VIBRATION (FOR MOBILE DEVICES)
  const vibrateDevice = () => {
    if ('vibrate' in navigator) {
      navigator.vibrate(200); // Vibrate for 200ms
    }
  };

  // Add new notification
  const addNotification = (notification) => {
    const newNotification = {
      id: Date.now(),
      ...notification,
      timestamp: new Date(),
      read: false
    };

    setNotifications(prev => [newNotification, ...prev]);
    setUnreadCount(prev => prev + 1);

    // ✅ PLAY SOUND AND VIBRATE
    playNotificationSound();
    vibrateDevice();

    // Show browser notification if permitted
    if (Notification.permission === 'granted') {
      new Notification(notification.title || 'New Notification', {
        body: notification.message,
        icon: '/favicon.ico',
        tag: 'p2p-notification'
      });
    }
  };

  // Mark notification as read
  const markAsRead = (id) => {
    setNotifications(prev =>
      prev.map(notif =>
        notif.id === id ? { ...notif, read: true } : notif
      )
    );
    setUnreadCount(prev => Math.max(0, prev - 1));
  };

  // Mark all as read
  const markAllAsRead = () => {
    setNotifications(prev => prev.map(notif => ({ ...notif, read: true })));
    setUnreadCount(0);
  };

  // Clear all notifications
  const clearAll = () => {
    setNotifications([]);
    setUnreadCount(0);
  };

  // Join user notification room
  const joinUserRoom = (userId) => {
    if (socket && userId) {
      socket.emit('join_user', userId);
      console.log(`🔔 Frontend: Joined notification room for user ${userId}`);
    }
  };

  return (
    <NotificationContext.Provider value={{
      notifications,
      unreadCount,
      addNotification,
      markAsRead,
      markAllAsRead,
      clearAll,
      socket,
      joinUserRoom,
      handleNotificationClick // ← ADD THIS
    }}>
      {children}
    </NotificationContext.Provider>
  );
};

