import React, { useState, useEffect } from 'react';
import Marketplace from './Marketplace';
import OrderPagee from './OrderPagee';
import MyAds from './MyAds/MyAds';
import NotificationBell from './NotificationBell/NotificationBell';
import './Dashboard.css';

const Dashboard = ({ user, onLogout }) => {
  const [activeTab, setActiveTab] = useState('marketplace');
  const [selectedOrderId, setSelectedOrderId] = useState(null);

  // Handle notification redirects
  useEffect(() => {
    const handleNotificationNavigation = (event) => {
      const { tab, orderId } = event.detail;
      console.log('🔔 Dashboard received navigation:', { tab, orderId });

      setActiveTab(tab);
      if (orderId) {
        setSelectedOrderId(orderId);
      }
    };

    // Listen for real-time navigation events
    window.addEventListener('notificationNavigate', handleNotificationNavigation);

    // Check for stored redirect data (when coming from notification click)
    const storedRedirect = localStorage.getItem('notificationRedirect');
    if (storedRedirect) {
      const redirectData = JSON.parse(storedRedirect);
      console.log('🔔 Processing stored redirect:', redirectData);

      setActiveTab(redirectData.tab);
      if (redirectData.orderId) {
        setSelectedOrderId(redirectData.orderId);
      }

      // Clear the stored data
      localStorage.removeItem('notificationRedirect');
    }

    return () => {
      window.removeEventListener('notificationNavigate', handleNotificationNavigation);
    };
  }, []);

  // Pass selectedOrderId to OrderPagee component
  const renderActiveTab = () => {
    switch (activeTab) {
      case 'marketplace':
        return <Marketplace />;
      case 'orders':
        return <OrderPagee selectedOrderId={selectedOrderId} />;
      case 'myads':
        return <MyAds />;
      case 'chat':
        return (
          <div className="chat-tab">
            <h3>Chat Messages</h3>
            <p>Chat functionality will be implemented here.</p>
            {selectedOrderId && (
              <p>Order ID: {selectedOrderId} would be opened here.</p>
            )}
          </div>
        );
      default:
        return <Marketplace />;
    }
  };

  return (
    <div className="dashboard">
      <header className="dashboard-header">
        <div className="header-left">
          <h1>🚀 P2P Exchange</h1>
          <span className="welcome-text">Welcome, {user?.name || user?.email}!</span>
        </div>
        <div className="header-right">
          <NotificationBell />
          <button className="logout-btn" onClick={onLogout}>
            Logout
          </button>
        </div>
      </header>

      <nav className="dashboard-nav">
        <button 
          className={activeTab === 'marketplace' ? 'active' : ''}
          onClick={() => {
            setActiveTab('marketplace');
            setSelectedOrderId(null);
          }}
        >
          🏪 Marketplace
        </button>
        <button 
          className={activeTab === 'orders' ? 'active' : ''}
          onClick={() => {
            setActiveTab('orders');
            setSelectedOrderId(null);
          }}
        >
          📦 My Orders
        </button>
        <button 
          className={activeTab === 'chat' ? 'active' : ''}
          onClick={() => setActiveTab('chat')}
        >
          💬 Chat
        </button>
        <button 
          className={activeTab === 'myads' ? 'active' : ''}
          onClick={() => setActiveTab('myads')}
        >
          📢 My Ads
        </button>
      </nav>

      <main className="dashboard-content">
        {renderActiveTab()}
      </main>
    </div>
  );
};

export default Dashboard;
