import React, { useState, useEffect, createContext, useContext } from 'react';
import { QueryClient, QueryClientProvider } from '@tanstack/react-query';
import Login from './components/Login';
import Register from './components/Register';
import Dashboard from './components/Dashboard';
import './App.css';

// ✅ CREATE NOTIFICATION CONTEXT
const NotificationContext = createContext();

// ✅ CUSTOM HOOK FOR NOTIFICATIONS
export const useNotifications = () => {
  const context = useContext(NotificationContext);
  if (!context) {
    throw new Error('useNotifications must be used within NotificationProvider');
  }
  return context;
};

// ✅ NOTIFICATION PROVIDER COMPONENT
const NotificationProvider = ({ children }) => {
  const [notifications, setNotifications] = useState([]);
  const [unreadCount, setUnreadCount] = useState(0);

  // Play notification sound
  const playNotificationSound = () => {
    const audio = new Audio('/notification.mp3'); // You'll need to add this sound file
    audio.play().catch(err => console.log('Audio play failed:', err));

    // Fallback: Use browser's built-in notification sound
    if (typeof window.Notification !== 'undefined') {
      // This will play the system notification sound
      const notification = new Notification('New Order', {
        silent: true // Rely on our audio file
      });
      setTimeout(() => notification.close(), 100);
    }
  };

  // Add new notification
  const addNotification = (notification) => {
    const newNotification = {
      id: Date.now(),
      ...notification,
      timestamp: new Date(),
      read: false
    };

    setNotifications(prev => [newNotification, ...prev]);
    setUnreadCount(prev => prev + 1);
    playNotificationSound();

    // Show browser notification if permitted
    if (Notification.permission === 'granted') {
      new Notification(notification.title, {
        body: notification.message,
        icon: '/favicon.ico'
      });
    }
  };

  // Mark notification as read
  const markAsRead = (id) => {
    setNotifications(prev =>
      prev.map(notif =>
        notif.id === id ? { ...notif, read: true } : notif
      )
    );
    setUnreadCount(prev => Math.max(0, prev - 1));
  };

  // Mark all as read
  const markAllAsRead = () => {
    setNotifications(prev => prev.map(notif => ({ ...notif, read: true })));
    setUnreadCount(0);
  };

  // Clear all notifications
  const clearAll = () => {
    setNotifications([]);
    setUnreadCount(0);
  };

  return (
    <NotificationContext.Provider value={{
      notifications,
      unreadCount,
      addNotification,
      markAsRead,
      markAllAsRead,
      clearAll
    }}>
      {children}
    </NotificationContext.Provider>
  );
};

// ✅ CREATE QUERY CLIENT
const queryClient = new QueryClient({
  defaultOptions: {
    queries: {
      refetchOnWindowFocus: false,
      retry: 1,
    },
  },
});

function App() {
  const [currentUser, setCurrentUser] = useState(null);
  const [showLogin, setShowLogin] = useState(true);
  const [loading, setLoading] = useState(true);

  // Request notification permission on app start
  useEffect(() => {
    if ('Notification' in window && Notification.permission === 'default') {
      Notification.requestPermission();
    }
  }, []);

  // Check if user is already logged in (on app start)
  useEffect(() => {
    const token = localStorage.getItem('token');
    const userData = localStorage.getItem('user');

    if (token && userData) {
      setCurrentUser(JSON.parse(userData));
    }
    setLoading(false);
  }, []);

  const handleLogin = (authData) => {
    setCurrentUser(authData.user);
    localStorage.setItem('token', authData.token);
    localStorage.setItem('user', JSON.stringify(authData.user));
    console.log('User logged in:', authData.user);
  };

  const handleLogout = () => {
    setCurrentUser(null);
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    queryClient.clear();
    console.log('User logged out');
  };

  if (loading) {
    return (
      <div className="app">
        <div className="container">
          <div className="loading">Loading...</div>
        </div>
      </div>
    );
  }

  return (
    <QueryClientProvider client={queryClient}>
      <NotificationProvider>
        <div className="app">
          {currentUser ? (
            <Dashboard user={currentUser} onLogout={handleLogout} />
          ) : (
            <div className="container">
              <div className="auth-container">
                <div className="auth-header">
                  <h1>🚀 P2P Currency Exchange</h1>
                  <p>Trade currencies securely with people worldwide</p>
                </div>

                {showLogin ? (
                  <>
                    <Login onLogin={handleLogin} />
                    <div className="auth-switch">
                      <p>Don't have an account?{' '}
                        <span className="switch-link" onClick={() => setShowLogin(false)}>
                          Sign up here
                        </span>
                      </p>
                    </div>
                  </>
                ) : (
                  <>
                    <Register onLogin={handleLogin} />
                    <div className="auth-switch">
                      <p>Already have an account?{' '}
                        <span className="switch-link" onClick={() => setShowLogin(true)}>
                          Login here
                        </span>
                      </p>
                    </div>
                  </>
                )}

                <div className="features">
                  <h3>Why Choose Our Platform?</h3>
                  <div className="feature-list">
                    <div className="feature">🔒 Secure Transactions</div>
                    <div className="feature">🌍 Global Marketplace</div>
                    <div className="feature">⚡ Fast Verification</div>
                    <div className="feature">💼 Multiple Payment Methods</div>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      </NotificationProvider>
    </QueryClientProvider>
  );
}

export default App;
